import type { Metadata, Viewport } from "next";
import localFont from "next/font/local";

import { siteConfig } from "@/config/site";
import { cn } from "@/lib/utils";

import { SiteFooter } from "@/components/layout/footer/site-footer";
import { SiteHeader } from "@/components/layout/header/site-header";

import { ThemeProvider } from "@/components/provider/theme-provider";

import { Toaster as ToasterSonner } from "@/components/ui/sonner";
import { Toaster } from "@/components/ui/toaster";

import "@/styles/globals.css";

const geistSans = localFont({
  src: "../../fonts/GeistVF.woff",
  variable: "--font-geist-sans",
  weight: "100 900",
});
const geistMono = localFont({
  src: "../../fonts/GeistMonoVF.woff",
  variable: "--font-geist-mono",
  weight: "100 900",
});

export const metadata: Metadata = {
  metadataBase: new URL(siteConfig.url),
  title: {
    default: siteConfig.name,
    template: `%s - ${siteConfig.name}`,
  },
  description: siteConfig.description,

  robots: {
    follow: false,
    index: false,
    nocache: false,
    googleBot: {
      index: false,
      follow: false,
      noimageindex: false,
    },
  },
  formatDetection: {
    email: false,
    address: false,
    telephone: false,
  },
  referrer: "origin-when-cross-origin",

  icons: {
    icon: siteConfig.favicon,
    shortcut: siteConfig.favicon16x16,
    apple: siteConfig.favicon180x180,
  },

  openGraph: {
    type: "website",
    title: siteConfig.name,
    locale: siteConfig.locale,
    description: siteConfig.description,
    url: siteConfig.url,
    siteName: siteConfig.name,
    images: [
      {
        url: siteConfig.favicon180x180,
        width: 180,
        height: 180,
      },
      {
        url: siteConfig.favicon192x192,
        width: 192,
        height: 192,
      },
      {
        url: siteConfig.favicon512x512,
        width: 512,
        height: 512,
      },
    ],
  },

  twitter: {
    card: "summary_large_image",
    title: siteConfig.name,
    description: siteConfig.description,
    images: [siteConfig.favicon512x512],
    creator: siteConfig.creator,
  },

  authors: [{ name: siteConfig.author, url: siteConfig.authorUrl }],

  creator: siteConfig.creator,
  publisher: siteConfig.publisher,
};

export const viewport: Viewport = {
  themeColor: [
    { media: "(prefers-color-scheme: light)", color: "white" },
    { media: "(prefers-color-scheme: dark)", color: "black" },
  ],
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" suppressHydrationWarning>
      <body
        className={cn(
          "min-h-screen bg-background antialiased",
          geistSans.variable,
          geistMono.variable,
        )}
      >
        <ThemeProvider
          attribute="class"
          defaultTheme="system"
          enableSystem
          disableTransitionOnChange
        >
          <div className="flex min-h-screen min-w-full flex-col">
            <SiteHeader siteHeader={siteConfig.siteHeader} />
            <div className="flex flex-1">
              <main className="flex flex-1 flex-col">{children}</main>
            </div>
            <SiteFooter footer={siteConfig.footerSimple} />
          </div>
          <ToasterSonner />
          <Toaster />
        </ThemeProvider>
      </body>
    </html>
  );
}

import { Typography } from "@/components/ui/typography";

export default function HomePage() {
  return (
    <div className="flex flex-1 flex-col">
      <section className="container p-2">
        <Typography.H1 className="text-2xl font-semibold lg:text-3xl">
          Home
        </Typography.H1>
      </section>
    </div>
  );
}

"use client";

import * as React from "react";

import { ThemeProvider as NextThemesProvider } from "next-themes";
import type { ThemeProviderProps } from "next-themes/dist/types";

export function ThemeProvider({
  children,
  ...props
}: Readonly<ThemeProviderProps>) {
  return <NextThemesProvider {...props}>{children}</NextThemesProvider>;
}

"use client";
import { useTheme } from "next-themes";

import { Moon, Sun } from "lucide-react";

import { cn } from "@/lib/utils";

import { Button } from "@/components/ui/button";

type ToggleThemeProps = {
  className?: string;
};

export function ToggleTheme({ className }: ToggleThemeProps) {
  const { setTheme, theme } = useTheme();

  return (
    <Button
      variant="ghost"
      size="icon"
      onClick={() => setTheme(theme === "light" ? "dark" : "light")}
      type="button"
      className={cn("size-9 text-muted-foreground", className)}
    >
      <Sun className="h-6 w-[1.3rem] dark:hidden" />
      <Moon className="hidden size-5 dark:block" />
      <span className="sr-only">Toggle theme</span>
    </Button>
  );
}

import Link from "next/link";

import { NavMain } from "@/components/layout/navbar/inc/nav-main";
import { NavMobile } from "@/components/layout/navbar/nav-mobile";
import { ToggleTheme } from "@/components/layout/toggle/toggle-theme";

import { Button } from "@/components/ui/button";

type SiteHeaderProps = {
  siteHeader: {
    name: string;
    links: {
      id: string;
      label: string;
      href: string;
      disabled: boolean;
    }[];
  };
};

export const SiteHeader = ({ siteHeader }: SiteHeaderProps) => {
  return (
    <header className="sticky top-0 z-50 w-full border-b border-border/40 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/95">
      <div className="mx-auto flex h-14 items-center justify-center px-2">
        <div className="container relative flex w-full items-center justify-between">
          {/* Left */}
          <div className="flex items-center space-x-2">
            <h1 className="text-3xl font-bold">{siteHeader.name}</h1>
            <NavMain navbar={siteHeader} className="hidden md:flex" />
          </div>

          {/* Right */}
          <div className="flex flex-1 items-center justify-end space-x-2">
            <Button asChild size="sm">
              <Link href="/auth/sign-in">Sign-in</Link>
            </Button>
            <ToggleTheme className="hidden md:flex" />
            <NavMobile className="flex md:hidden" />
          </div>
        </div>
      </div>
    </header>
  );
};

"use client";

import { cn } from "@/lib/utils";

import {
  NavigationMenu,
  NavigationMenuItem,
  NavigationMenuList,
} from "@/components/ui/navigation-menu";

import { NavLink } from "./nav-link";

interface NavMainProps {
  className?: string;
  navbar: {
    name: string;
    links: {
      id: string;
      label: string;
      href: string;
    }[];
  };
}

export const NavMain = ({ className, navbar }: NavMainProps) => {
  return (
    <NavigationMenu>
      <NavigationMenuList className={cn("flex items-center", className)}>
        {navbar.links.map((item) => (
          <NavigationMenuItem
            className="flex items-center text-lg font-medium text-muted-foreground md:block"
            key={item.id}
          >
            <NavLink href={item.href}>{item.label}</NavLink>
          </NavigationMenuItem>
        ))}
      </NavigationMenuList>
    </NavigationMenu>
  );
};

"use client";

import type { ReactNode } from "react";
import React from "react";

import Link from "next/link";
import { usePathname } from "next/navigation";

import { cn } from "@/lib/utils";

import { Button } from "@/components/ui/button";

interface NavLinkProps {
  href: string;
  children: ReactNode;
}
export const NavLink = ({
  href,
  children,
}: NavLinkProps): React.JSX.Element => {
  const pathname = usePathname();

  return (
    <Button
      asChild
      variant="ghost"
      size="sm"
      className="text-lg hover:bg-transparent"
    >
      <Link
        className={cn(pathname === href ? "text-primary" : null)}
        href={href}
      >
        {children}
      </Link>
    </Button>
  );
};

"use client";

import { MenuIcon, X } from "lucide-react";

import { siteConfig } from "@/config/site";
