import type { Metadata, Viewport } from "next";
import localFont from "next/font/local";

import { siteConfig } from "@/config/site";
import { cn } from "@/lib/utils";

import { SiteFooter } from "@/components/layout/footer/site-footer";
import { SiteHeader } from "@/components/layout/header/site-header";

import { ThemeProvider } from "@/components/provider/theme-provider";

import { Toaster as ToasterSonner } from "@/components/ui/sonner";
import { Toaster } from "@/components/ui/toaster";
import { ToasterTheme } from "@/components/ui/toaster-theme";
import "@/styles/globals.css";

const geistSans = localFont({
  src: "../../assets/fonts/GeistVF.woff",
  variable: "--font-geist-sans",
  weight: "100 900",
});
const geistMono = localFont({
  src: "../../assets/fonts/GeistMonoVF.woff",
  variable: "--font-geist-mono",
  weight: "100 900",
});

export const metadata: Metadata = {
  metadataBase: new URL(siteConfig.url),
  title: {
    default: siteConfig.name,
    template: `%s - ${siteConfig.name}`,
  },
  description: siteConfig.siteDescription,

  robots: {
    follow: false,
    index: false,
    nocache: false,
    googleBot: {
      index: false,
      follow: false,
      noimageindex: false,
    },
  },
  formatDetection: {
    email: false,
    address: false,
    telephone: false,
  },
  referrer: "origin-when-cross-origin",

  icons: {
    icon: siteConfig.favicon,
    shortcut: siteConfig.favicon16x16,
    apple: siteConfig.favicon180x180,
  },

  openGraph: {
    type: "website",
    title: siteConfig.name,
    locale: siteConfig.locale,
    description: siteConfig.siteDescription,
    url: siteConfig.url,
    siteName: siteConfig.name,
    images: [
      {
        url: siteConfig.favicon180x180,
        width: 180,
        height: 180,
      },
      {
        url: siteConfig.favicon192x192,
        width: 192,
        height: 192,
      },
      {
        url: siteConfig.favicon512x512,
        width: 512,
        height: 512,
      },
    ],
  },

  twitter: {
    card: "summary_large_image",
    title: siteConfig.name,
    description: siteConfig.siteDescription,
    images: [siteConfig.favicon512x512],
    creator: siteConfig.creator,
  },

  authors: [{ name: siteConfig.author, url: siteConfig.authorUrl }],

  creator: siteConfig.creator,
  publisher: siteConfig.publisher,
};

export const viewport: Viewport = {
  themeColor: [
    { media: "(prefers-color-scheme: light)", color: "white" },
    { media: "(prefers-color-scheme: dark)", color: "black" },
  ],
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" suppressHydrationWarning>
      <body
        className={cn(
          "min-h-screen bg-background antialiased",
          geistSans.variable,
          geistMono.variable,
        )}
      >
        <ThemeProvider
          attribute="class"
          defaultTheme="system"
          enableSystem
          disableTransitionOnChange
        >
          <div className="flex min-h-screen min-w-full flex-col">
            <SiteHeader siteHeader={siteConfig.siteHeader} />
            <div className="flex flex-1">
              <main className="flex flex-1 flex-col">{children}</main>
            </div>
            <SiteFooter footer={siteConfig.footerSimple} />
          </div>
          <ToasterSonner />
          <Toaster />
          <ToasterTheme />
        </ThemeProvider>
      </body>
    </html>
  );
}

import { LoadingSpinner } from "@/components/layout/loading/loading-spinner";

export default function Loading() {
  return (
    <section className="flex flex-col items-center gap-64 py-72">
      <div className="container flex h-full flex-col items-center justify-center space-y-2">
        <LoadingSpinner />
      </div>
    </section>
  );
}

import { siteConfig } from "@/config/site";

import { Container } from "@/components/layout/container/container";
import { HeroSimple } from "@/components/layout/hero/hero-simple";

export default function HomePage() {
  return (
    <Container size="full">
      <HeroSimple hero={siteConfig.heroSimple} />
    </Container>
  );
}

export type SiteConfig = typeof siteConfig;

const name = "AdminSys";

const siteDescription =
  "Nextjs App with Typescript, Tailwind CSS, Shadcn/ui, ESLint, Prettier, and more.";

const emailContact = "contact@email.com";
const emailSupport = "support@email.com";

const url = "https://adminsys-boilerplate.vercel.app";
const favicon = "/favicon.ico";
const favicon16x16 = "/assets/images/icon/icon-16x16.png";
const favicon180x180 = "/assets/images/icon/icon-180x180.png";
const favicon192x192 = "/assets/images/icon/icon-192x192.png";
const favicon512x512 = "/assets/images/icon/icon-512x512.png";
const logo = "/assets/images/logo/logo-dark.png";
const logoLight = "/assets/images/logo/logo-light.png";
const logoDark = "/assets/images/logo/logo-dark.png";

const locale = "en-US";

const facebook = "https://www.facebook.com/";
const instagram = "https://www.instagram.com/";
const youtube = "https://www.youtube.com/";

const keywords = "React, Next.js, TypeScript";

const author = "MaskSysDev";
const authorUrl = "https://github.com/MaskSysDev";

const creator = "Mask";
const publisher = "MaskSysDev";

const projectUrl = "https://github.com/MaskSysDev/adminsys-boilerplate";

export const siteConfig = {
  /* Global */
  name,
  siteDescription,
  emailContact,
  emailSupport,
  url,

  favicon,
  favicon16x16,
  favicon180x180,
  favicon192x192,
  favicon512x512,
  logo,
  logoLight,
  logoDark,

  locale,

  keywords,

  author,
  authorUrl,

  creator,
  publisher,

  project: {
    url: projectUrl,
    author,
    authorUrl,
  },
  docsRepositoryBase: projectUrl,

  links: {
    github: projectUrl,
    facebook,
    instagram,
    youtube,
  },

  /* Components Layout */
  siteHeader: {
    name,
    position: "end",
    links: [
      {
        id: "1",
        label: "Home",
        href: "/",
        disabled: false,
      },
      {
        id: "2",
        label: "About",
        href: "/about",
        disabled: false,
      },
      {
        id: "3",
        label: "Contact",
        href: "/contact",
        disabled: false,
      },
    ],
  },

  footerSimple: {
    author,
    authorUrl,
    name,
    facebook,
    instagram,
    youtube,
  },

  heroSimple: {
    size: "64",
    title: name,
    subtitle: "Template ",
    subtitleColor: "Admin",
    description: siteDescription,
    labelLink1: "Get Started",
    link1: "/",
    labelLink2: "GitHub",
    link2: projectUrl,
    backgroundImage: "image",
    backgroundColor: "rgba(0, 0, 0, 0.0)",
    backgroundColorTop: "rgba(0, 0, 0, 0.0)",
    backgroundColorBottom: "rgba(0, 0, 0, 0.0)",
  },
};

import Link from "next/link";

import { cn } from "@/lib/utils";

import { Container } from "@/components/layout/container/container";
import { Logo } from "@/components/layout/logo/logo";
import { NavMain } from "@/components/layout/navbar/inc/nav-main";
import { NavMobile } from "@/components/layout/navbar/nav-mobile";
import { ToggleTheme } from "@/components/layout/toggle/toggle-theme";

import { buttonVariants } from "@/components/ui/button";

type SiteHeaderProps = {
  siteHeader: {
    name: string;
    position?: string;
    links: {
      id: string;
      label: string;
      href: string;
      disabled: boolean;
    }[];
  };
};

export const SiteHeader = ({ siteHeader }: SiteHeaderProps) => {
  return (
    <header className="sticky top-0 z-50 w-full border-b border-border/40 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/95">
      <Container className="flex h-14 items-center justify-center">
        <div className="relative flex w-full items-center justify-between">
          {/* Left */}
          <div className="flex items-center space-x-2">
            <Link
              href="/"
              className={cn(
                buttonVariants({
                  variant: "ghost",
                }),
                "flex items-center space-x-2 px-0 hover:bg-transparent",
              )}
            >
              <Logo />
            </Link>
          </div>

          {/* Center */}
          <div
            className={cn("flex w-full items-center justify-start px-4", {
              "justify-start":
                siteHeader.position === "start" ||
                siteHeader.position === "left",
              "justify-center": siteHeader.position === "center",
              "justify-end":
                siteHeader.position === "end" ||
                siteHeader.position === "right",
            })}
          >
            <NavMain navbar={siteHeader} className="hidden md:flex" />
          </div>

          {/* Right */}
          <div className="flex flex-1 items-center justify-end space-x-2">
            <ToggleTheme className="hidden md:flex" />
            <NavMobile className="flex md:hidden" />
          </div>
        </div>
      </Container>
    </header>
  );
};
