import "prismjs/components/prism-typescript";

import "@/styles/code-block.css";

interface CodeBlockProps {
  code: string;
  language: string;
}

export function CodeBlock({ code, language }: CodeBlockProps) {
  const [isCopied, setIsCopied] = useState(false);

  useEffect(() => {
    Prism.highlightAll();
  }, [code, language]);

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setIsCopied(true);
      setTimeout(() => setIsCopied(false), 2000);
    } catch (err) {
      console.error("Failed to copy text: ", err);
    }
  };

  return (
    <div className="group relative w-full overflow-auto bg-muted/60">
      <Button
        onClick={copyToClipboard}
        className="absolute right-4 top-2 z-10 hidden size-8 text-muted-foreground group-hover:flex"
        size="icon"
        variant="outline"
        aria-label={isCopied ? "Código copiado" : "Copiar código"}
      >
        {isCopied ? <Check className="size-4" /> : <Copy className="size-4" />}
      </Button>
      <pre className="mt-4 max-h-96 w-full">
        <code className={`language-${language}`}>{code}</code>
      </pre>
    </div>
  );
}

import * as React from "react";

import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const containerVariants = cva("px-4", {
  variants: {
    center: {
      true: "mx-auto",
    },
    size: {
      sm: "container max-w-screen-sm",
      md: "container max-w-screen-md",
      lg: "container max-w-screen-lg",
      xl: "container max-w-screen-xl",
      "2xl": "container max-w-screen-2xl",
      full: "w-full",
    },
  },
  defaultVariants: {
    size: "xl",
    center: true,
  },
});

export interface ContainerProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof containerVariants> {}

function Container({ className, size, center, ...props }: ContainerProps) {
  return (
    <div
      className={cn(containerVariants({ size, center }), className)}
      {...props}
    />
  );
}

export { Container, containerVariants };

